SELECT customer.c_mktsegment, customer.c_name, SUM(customer.c_nationkey) AS sum_customer_c_nationkey, VARIANCE(customer.c_custkey) AS variance_customer_c_custkey, SUM(customer.c_nationkey) AS sum_customer_c_nationkey, VARIANCE(customer.c_nationkey) AS variance_customer_c_nationkey, COUNT(customer.c_custkey) AS count_customer_c_custkey FROM customer WHERE customer.c_comment = 'TXKDJ' GROUP BY customer.c_mktsegment, customer.c_name HAVING COUNT(*) > 6096 AND COUNT(customer.c_custkey) > 47963 ORDER BY customer.c_mktsegment, customer.c_name