SELECT nation.n_nationkey, AVG(nation.n_regionkey) AS avg_n_regionkey, MAX(nation.n_regionkey) AS max_n_regionkey, COUNT(nation.n_nationkey) AS count_n_nationkey FROM nation WHERE nation.n_name LIKE '%C' AND nation.n_name LIKE '%C' AND nation.n_nationkey < 882 AND nation.n_comment LIKE '%B%' AND nation.n_comment LIKE '%B%' GROUP BY nation.n_nationkey HAVING COUNT(*) > 582 ORDER BY nation.n_nationkey