SELECT region.r_comment, region.r_name, STDDEV(region.r_regionkey) AS stddev_region_r_regionkey, STDDEV(region.r_regionkey) AS stddev_region_r_regionkey, AVG(region.r_regionkey) AS avg_region_r_regionkey, VARIANCE(region.r_regionkey) AS variance_region_r_regionkey FROM region WHERE (region.r_regionkey BETWEEN 238 AND 851 AND region.r_name = 'IEJYJ') OR (region.r_comment = 'GTKEU') GROUP BY region.r_comment, region.r_name ORDER BY region.r_comment, region.r_name