SELECT region.r_name, STDDEV(region.r_regionkey) AS stddev_region_r_regionkey, VARIANCE(region.r_regionkey) AS variance_region_r_regionkey, AVG(region.r_regionkey) AS avg_region_r_regionkey FROM region WHERE (((region.r_name = 'LNCZK') OR (region.r_comment LIKE '%B%')) OR (region.r_regionkey = 734)) OR (region.r_comment LIKE '%B%') GROUP BY region.r_name HAVING COUNT(*) > 5898 AND AVG(region.r_regionkey) > 12447 ORDER BY region.r_name