SELECT part.p_brand, part.p_comment, part.p_container, MAX(part.p_retailprice) AS max_part_p_retailprice, AVG(part.p_size) AS avg_part_p_size, COUNT(part.p_partkey) AS count_part_p_partkey, MIN(part.p_size) AS min_part_p_size FROM part WHERE ((part.p_partkey > 809) OR (part.p_type = 'AUILJ')) OR (part.p_size = 585) GROUP BY part.p_brand, part.p_comment, part.p_container HAVING COUNT(*) > 3716 AND AVG(part.p_size) > 66477 ORDER BY part.p_container, part.p_brand