SELECT nation.n_comment, SUM(nation.n_regionkey) AS sum_n_regionkey, MIN(nation.n_nationkey) AS min_n_nationkey, MIN(nation.n_regionkey) AS min_n_regionkey FROM nation WHERE nation.n_name LIKE '%C' AND nation.n_comment LIKE 'A%' AND nation.n_name LIKE '%C' AND nation.n_comment LIKE 'A%' AND nation.n_regionkey = 865 GROUP BY nation.n_comment HAVING COUNT(*) > 319 ORDER BY nation.n_comment