SELECT customer.c_address, MIN(customer.c_custkey) AS min_customer_c_custkey, STDDEV(customer.c_custkey) AS stddev_customer_c_custkey, SUM(customer.c_custkey) AS sum_customer_c_custkey, VARIANCE(customer.c_nationkey) AS variance_customer_c_nationkey, COUNT(customer.c_acctbal) AS count_customer_c_acctbal FROM customer WHERE customer.c_mktsegment = 'GUNUC' GROUP BY customer.c_address ORDER BY customer.c_address