SELECT part.p_comment, MAX(part.p_retailprice) AS max_part_p_retailprice, MIN(part.p_partkey) AS min_part_p_partkey, VARIANCE(part.p_retailprice) AS variance_part_p_retailprice, MAX(part.p_size) AS max_part_p_size, VARIANCE(part.p_retailprice) AS variance_part_p_retailprice FROM part WHERE part.p_partkey > 235 GROUP BY part.p_comment HAVING COUNT(*) > 4337 AND MAX(part.p_retailprice) > 76249 ORDER BY part.p_comment