SELECT nation.n_comment, COUNT(nation.n_regionkey) AS count_nation_n_regionkey, MIN(nation.n_regionkey) AS min_nation_n_regionkey, AVG(nation.n_regionkey) AS avg_nation_n_regionkey, VARIANCE(nation.n_regionkey) AS variance_nation_n_regionkey FROM nation WHERE nation.n_regionkey BETWEEN 154 AND 765 AND nation.n_name LIKE '%C' AND nation.n_regionkey > 215 AND nation.n_name = 'SVCDV' GROUP BY nation.n_comment HAVING COUNT(*) > 216 AND AVG(nation.n_regionkey) > 81897 ORDER BY nation.n_comment