SELECT supplier.s_name, supplier.s_comment, COUNT(supplier.s_acctbal) AS count_supplier_s_acctbal, SUM(supplier.s_suppkey) AS sum_supplier_s_suppkey, MIN(supplier.s_acctbal) AS min_supplier_s_acctbal FROM supplier WHERE (supplier.s_phone = 'ENWYN') OR (supplier.s_comment LIKE '%B%') AND supplier.s_comment = 'VTNXD' GROUP BY supplier.s_name, supplier.s_comment ORDER BY supplier.s_name, supplier.s_comment