SELECT orders.o_comment, orders.o_clerk, orders.o_orderstatus, STDDEV(orders.o_shippriority) AS stddev_orders_o_shippriority, VARIANCE(orders.o_totalprice) AS variance_orders_o_totalprice, COUNT(orders.o_orderkey) AS count_orders_o_orderkey FROM orders WHERE orders.o_orderkey > 612 AND orders.o_orderstatus LIKE '%B%' GROUP BY orders.o_comment, orders.o_clerk, orders.o_orderstatus ORDER BY orders.o_comment, orders.o_clerk