SELECT orders.o_orderstatus, MAX(orders.o_totalprice) AS max_orders_o_totalprice, COUNT(orders.o_custkey) AS count_orders_o_custkey, MAX(orders.o_custkey) AS max_orders_o_custkey, STDDEV(orders.o_totalprice) AS stddev_orders_o_totalprice FROM orders WHERE (orders.o_shippriority = 425) OR (orders.o_comment = 'KJQRR') GROUP BY orders.o_orderstatus HAVING COUNT(*) > 8745 AND MAX(orders.o_totalprice) > 13594 ORDER BY orders.o_orderstatus