SELECT supplier.s_address, supplier.s_name, VARIANCE(supplier.s_nationkey) AS variance_supplier_s_nationkey, MIN(supplier.s_suppkey) AS min_supplier_s_suppkey, VARIANCE(supplier.s_suppkey) AS variance_supplier_s_suppkey, MAX(supplier.s_suppkey) AS max_supplier_s_suppkey, VARIANCE(supplier.s_suppkey) AS variance_supplier_s_suppkey FROM supplier WHERE supplier.s_name = 'EPGOB' GROUP BY supplier.s_address, supplier.s_name ORDER BY supplier.s_address, supplier.s_name