SELECT partsupp.ps_comment, AVG(partsupp.ps_availqty) AS avg_partsupp_ps_availqty, MAX(partsupp.ps_partkey) AS max_partsupp_ps_partkey FROM partsupp WHERE (partsupp.ps_availqty = 319) OR (partsupp.ps_comment = 'CBBXY') AND partsupp.ps_suppkey < 664 GROUP BY partsupp.ps_comment ORDER BY partsupp.ps_comment