SELECT lineitem.l_linestatus, lineitem.l_receiptdate, VARIANCE(lineitem.l_tax) AS variance_lineitem_l_tax, SUM(lineitem.l_extendedprice) AS sum_lineitem_l_extendedprice, MAX(lineitem.l_orderkey) AS max_lineitem_l_orderkey, VARIANCE(lineitem.l_quantity) AS variance_lineitem_l_quantity, STDDEV(lineitem.l_suppkey) AS stddev_lineitem_l_suppkey FROM lineitem GROUP BY lineitem.l_linestatus, lineitem.l_receiptdate ORDER BY lineitem.l_linestatus, lineitem.l_receiptdate