SELECT orders.o_orderdate, orders.o_orderstatus, orders.o_comment, STDDEV(orders.o_totalprice) AS stddev_orders_o_totalprice, SUM(orders.o_custkey) AS sum_orders_o_custkey, AVG(orders.o_custkey) AS avg_orders_o_custkey FROM orders WHERE orders.o_orderkey = 434 GROUP BY orders.o_orderdate, orders.o_orderstatus, orders.o_comment HAVING COUNT(*) > 3834 AND STDDEV(orders.o_totalprice) > 67767 ORDER BY orders.o_orderstatus, orders.o_comment