SELECT supplier.s_name, supplier.s_comment, SUM(supplier.s_suppkey) AS sum_supplier_s_suppkey, COUNT(supplier.s_suppkey) AS count_supplier_s_suppkey, MAX(supplier.s_acctbal) AS max_supplier_s_acctbal, STDDEV(supplier.s_suppkey) AS stddev_supplier_s_suppkey FROM supplier WHERE supplier.s_suppkey < 535 AND supplier.s_phone = 'GAVFH' AND supplier.s_address LIKE 'A%' AND supplier.s_comment LIKE 'A%' GROUP BY supplier.s_name, supplier.s_comment ORDER BY supplier.s_name, supplier.s_comment