SELECT supplier.s_address, VARIANCE(supplier.s_suppkey) AS variance_supplier_s_suppkey, AVG(supplier.s_acctbal) AS avg_supplier_s_acctbal, MAX(supplier.s_nationkey) AS max_supplier_s_nationkey, STDDEV(supplier.s_nationkey) AS stddev_supplier_s_nationkey FROM supplier WHERE (supplier.s_phone LIKE 'A%') OR (supplier.s_suppkey BETWEEN 470 AND 503) AND supplier.s_comment = 'IAEJA' GROUP BY supplier.s_address HAVING COUNT(*) > 5229 AND MAX(supplier.s_nationkey) > 17140 ORDER BY supplier.s_address