SELECT orders.o_orderstatus, orders.o_orderpriority, AVG(orders.o_shippriority) AS avg_orders_o_shippriority, MIN(orders.o_orderkey) AS min_orders_o_orderkey FROM orders WHERE (orders.o_orderpriority LIKE '%C' AND orders.o_orderpriority = 'EHFPH') OR (orders.o_orderkey BETWEEN 271 AND 992) GROUP BY orders.o_orderstatus, orders.o_orderpriority HAVING COUNT(*) > 7760 AND AVG(orders.o_shippriority) > 41953 ORDER BY orders.o_orderpriority, orders.o_orderstatus