SELECT customer.c_address, customer.c_comment, customer.c_mktsegment, MIN(customer.c_nationkey) AS min_customer_c_nationkey, COUNT(customer.c_nationkey) AS count_customer_c_nationkey, COUNT(customer.c_acctbal) AS count_customer_c_acctbal, VARIANCE(customer.c_acctbal) AS variance_customer_c_acctbal, COUNT(customer.c_nationkey) AS count_customer_c_nationkey FROM customer WHERE customer.c_name = 'NIMCD' AND customer.c_name = 'SWPAG' GROUP BY customer.c_address, customer.c_comment, customer.c_mktsegment HAVING COUNT(*) > 1597 AND MIN(customer.c_nationkey) > 94095 ORDER BY customer.c_comment, customer.c_address