SELECT partsupp.ps_comment, MAX(partsupp.ps_availqty) AS max_partsupp_ps_availqty, COUNT(partsupp.ps_supplycost) AS count_partsupp_ps_supplycost, STDDEV(partsupp.ps_partkey) AS stddev_partsupp_ps_partkey, STDDEV(partsupp.ps_partkey) AS stddev_partsupp_ps_partkey, MAX(partsupp.ps_availqty) AS max_partsupp_ps_availqty FROM partsupp WHERE (((partsupp.ps_availqty = 860) OR (partsupp.ps_comment LIKE '%B%')) OR (partsupp.ps_availqty < 275)) OR (partsupp.ps_availqty > 74) GROUP BY partsupp.ps_comment HAVING COUNT(*) > 6707 AND STDDEV(partsupp.ps_partkey) > 69487 ORDER BY partsupp.ps_comment