SELECT region.r_name, COUNT(region.r_regionkey) AS count_r_regionkey, MIN(region.r_regionkey) AS min_r_regionkey, MIN(region.r_regionkey) AS min_r_regionkey FROM region WHERE region.r_regionkey BETWEEN 61 AND 389 AND region.r_comment LIKE '%B%' AND region.r_name = 'NFVOC' AND region.r_comment = 'MQOCH' AND region.r_name = 'KZQVU' GROUP BY region.r_name HAVING COUNT(*) > 295 ORDER BY region.r_name