SELECT nation.n_nationkey, MIN(nation.n_nationkey) AS min_n_nationkey, AVG(nation.n_nationkey) AS avg_n_nationkey, MIN(nation.n_nationkey) AS min_n_nationkey FROM nation WHERE nation.n_nationkey < 985 AND nation.n_name = 'NNBGI' AND nation.n_comment = 'UJZKE' AND nation.n_regionkey BETWEEN 9 AND 18 AND nation.n_nationkey < 635 GROUP BY nation.n_nationkey ORDER BY nation.n_nationkey