SELECT lineitem.l_commitdate, VARIANCE(lineitem.l_quantity) AS variance_lineitem_l_quantity, MAX(lineitem.l_discount) AS max_lineitem_l_discount, MIN(lineitem.l_linenumber) AS min_lineitem_l_linenumber, MIN(lineitem.l_extendedprice) AS min_lineitem_l_extendedprice, MAX(lineitem.l_partkey) AS max_lineitem_l_partkey FROM lineitem WHERE (lineitem.l_returnflag LIKE '%B%') OR (lineitem.l_commitdate >= '2019-01-25') GROUP BY lineitem.l_commitdate HAVING COUNT(*) > 1553 AND MAX(lineitem.l_partkey) > 34881 ORDER BY lineitem.l_commitdate