SELECT customer.c_name, customer.c_address, customer.c_comment, STDDEV(customer.c_nationkey) AS stddev_customer_c_nationkey, VARIANCE(customer.c_nationkey) AS variance_customer_c_nationkey, VARIANCE(customer.c_custkey) AS variance_customer_c_custkey, STDDEV(customer.c_acctbal) AS stddev_customer_c_acctbal FROM customer WHERE customer.c_comment = 'UFLDE' AND customer.c_mktsegment LIKE 'A%' GROUP BY customer.c_name, customer.c_address, customer.c_comment HAVING COUNT(*) > 4349 AND STDDEV(customer.c_nationkey) > 97440 ORDER BY customer.c_name, customer.c_comment