SELECT orders.o_orderdate, orders.o_orderpriority, orders.o_comment, MIN(orders.o_orderkey) AS min_orders_o_orderkey, AVG(orders.o_totalprice) AS avg_orders_o_totalprice, MAX(orders.o_custkey) AS max_orders_o_custkey, MIN(orders.o_orderkey) AS min_orders_o_orderkey, MAX(orders.o_totalprice) AS max_orders_o_totalprice FROM orders WHERE orders.o_orderpriority LIKE '%C' GROUP BY orders.o_orderdate, orders.o_orderpriority, orders.o_comment ORDER BY orders.o_orderpriority, orders.o_orderdate