SELECT supplier.s_comment, supplier.s_address, AVG(supplier.s_suppkey) AS avg_supplier_s_suppkey, MIN(supplier.s_nationkey) AS min_supplier_s_nationkey, STDDEV(supplier.s_acctbal) AS stddev_supplier_s_acctbal, STDDEV(supplier.s_suppkey) AS stddev_supplier_s_suppkey, MAX(supplier.s_acctbal) AS max_supplier_s_acctbal FROM supplier WHERE supplier.s_nationkey = 138 AND supplier.s_comment LIKE '%B%' GROUP BY supplier.s_comment, supplier.s_address HAVING COUNT(*) > 6199 AND STDDEV(supplier.s_acctbal) > 64904 ORDER BY supplier.s_comment, supplier.s_address