SELECT lineitem.l_shipmode, AVG(lineitem.l_quantity) AS avg_lineitem_l_quantity, VARIANCE(lineitem.l_discount) AS variance_lineitem_l_discount, VARIANCE(lineitem.l_orderkey) AS variance_lineitem_l_orderkey, AVG(lineitem.l_tax) AS avg_lineitem_l_tax FROM lineitem WHERE lineitem.l_linestatus LIKE '%B%' GROUP BY lineitem.l_shipmode HAVING COUNT(*) > 6158 AND VARIANCE(lineitem.l_orderkey) > 55523 ORDER BY lineitem.l_shipmode