SELECT supplier.s_name, MAX(supplier.s_suppkey) AS max_supplier_s_suppkey, MAX(supplier.s_suppkey) AS max_supplier_s_suppkey, STDDEV(supplier.s_nationkey) AS stddev_supplier_s_nationkey, SUM(supplier.s_acctbal) AS sum_supplier_s_acctbal FROM supplier WHERE (supplier.s_comment LIKE 'A%') OR (supplier.s_address LIKE '%C') AND supplier.s_phone = 'YDYWW' GROUP BY supplier.s_name ORDER BY supplier.s_name