SELECT nation.n_name, SUM(nation.n_regionkey) AS sum_nation_n_regionkey, VARIANCE(nation.n_nationkey) AS variance_nation_n_nationkey, STDDEV(nation.n_nationkey) AS stddev_nation_n_nationkey FROM nation WHERE (nation.n_nationkey > 683 AND nation.n_nationkey > 89 AND nation.n_regionkey > 214) OR (nation.n_comment = 'KQSWS') GROUP BY nation.n_name HAVING COUNT(*) > 3656 AND VARIANCE(nation.n_nationkey) > 78516 ORDER BY nation.n_name