SELECT supplier.s_phone, MAX(supplier.s_acctbal) AS max_supplier_s_acctbal, COUNT(supplier.s_acctbal) AS count_supplier_s_acctbal, COUNT(supplier.s_nationkey) AS count_supplier_s_nationkey, MAX(supplier.s_suppkey) AS max_supplier_s_suppkey FROM supplier WHERE supplier.s_nationkey < 296 GROUP BY supplier.s_phone HAVING COUNT(*) > 8603 AND COUNT(supplier.s_nationkey) > 27134 ORDER BY supplier.s_phone