SELECT region.r_comment, STDDEV(region.r_regionkey) AS stddev_region_r_regionkey, MIN(region.r_regionkey) AS min_region_r_regionkey, SUM(region.r_regionkey) AS sum_region_r_regionkey, STDDEV(region.r_regionkey) AS stddev_region_r_regionkey FROM region WHERE region.r_regionkey < 728 AND region.r_name LIKE '%C' AND region.r_comment LIKE '%C' GROUP BY region.r_comment HAVING COUNT(*) > 5955 AND SUM(region.r_regionkey) > 94819 ORDER BY region.r_comment