SELECT region.r_name, MIN(region.r_regionkey) AS min_region_r_regionkey, MIN(region.r_regionkey) AS min_region_r_regionkey, MIN(region.r_regionkey) AS min_region_r_regionkey, VARIANCE(region.r_regionkey) AS variance_region_r_regionkey, MAX(region.r_regionkey) AS max_region_r_regionkey FROM region WHERE ((region.r_regionkey = 27) OR (region.r_comment LIKE '%C')) OR (region.r_regionkey > 402) GROUP BY region.r_name ORDER BY region.r_name