SELECT orders.o_clerk, orders.o_orderdate, orders.o_orderstatus, SUM(orders.o_orderkey) AS sum_orders_o_orderkey, SUM(orders.o_orderkey) AS sum_orders_o_orderkey, COUNT(orders.o_custkey) AS count_orders_o_custkey, MAX(orders.o_shippriority) AS max_orders_o_shippriority, SUM(orders.o_shippriority) AS sum_orders_o_shippriority FROM orders WHERE (orders.o_orderstatus LIKE '%B%' AND orders.o_orderkey < 798 AND orders.o_orderkey > 799) OR (orders.o_clerk LIKE '%C') GROUP BY orders.o_clerk, orders.o_orderdate, orders.o_orderstatus ORDER BY orders.o_orderstatus, orders.o_clerk