SELECT partsupp.ps_comment, SUM(partsupp.ps_suppkey) AS sum_partsupp_ps_suppkey, STDDEV(partsupp.ps_suppkey) AS stddev_partsupp_ps_suppkey, VARIANCE(partsupp.ps_availqty) AS variance_partsupp_ps_availqty, SUM(partsupp.ps_partkey) AS sum_partsupp_ps_partkey FROM partsupp WHERE ((partsupp.ps_availqty BETWEEN 31 AND 421) OR (partsupp.ps_availqty BETWEEN 589 AND 967)) OR (partsupp.ps_availqty > 884) GROUP BY partsupp.ps_comment ORDER BY partsupp.ps_comment