SELECT lineitem.l_returnflag, lineitem.l_linestatus, COUNT(lineitem.l_discount) AS count_lineitem_l_discount, SUM(lineitem.l_tax) AS sum_lineitem_l_tax, VARIANCE(lineitem.l_tax) AS variance_lineitem_l_tax, MAX(lineitem.l_tax) AS max_lineitem_l_tax FROM lineitem WHERE lineitem.l_shipmode = 'KPSQF' GROUP BY lineitem.l_returnflag, lineitem.l_linestatus HAVING COUNT(*) > 7242 AND COUNT(lineitem.l_discount) > 14085 ORDER BY lineitem.l_linestatus, lineitem.l_returnflag