SELECT customer.c_phone, AVG(customer.c_nationkey) AS avg_c_nationkey, MIN(customer.c_nationkey) AS min_c_nationkey, MIN(customer.c_custkey) AS min_c_custkey FROM customer WHERE customer.c_mktsegment = 'NWYRK' AND customer.c_nationkey BETWEEN 112 AND 698 AND customer.c_name = 'QICWO' AND customer.c_mktsegment = 'NDVQM' GROUP BY customer.c_phone HAVING COUNT(*) > 407 ORDER BY customer.c_phone