SELECT orders.o_clerk, orders.o_orderdate, MAX(orders.o_totalprice) AS max_orders_o_totalprice, STDDEV(orders.o_custkey) AS stddev_orders_o_custkey, STDDEV(orders.o_totalprice) AS stddev_orders_o_totalprice FROM orders WHERE (orders.o_clerk LIKE '%B%' AND orders.o_orderkey BETWEEN 374 AND 485) OR (orders.o_orderkey > 250) GROUP BY orders.o_clerk, orders.o_orderdate HAVING COUNT(*) > 9289 AND MAX(orders.o_totalprice) > 86199 ORDER BY orders.o_orderdate, orders.o_clerk