SELECT region.r_comment, VARIANCE(region.r_regionkey) AS variance_region_r_regionkey, VARIANCE(region.r_regionkey) AS variance_region_r_regionkey, SUM(region.r_regionkey) AS sum_region_r_regionkey, SUM(region.r_regionkey) AS sum_region_r_regionkey FROM region WHERE ((region.r_name LIKE 'A%') OR (region.r_comment = 'OVDTJ')) OR (region.r_regionkey < 752) GROUP BY region.r_comment HAVING COUNT(*) > 5936 AND SUM(region.r_regionkey) > 25044 ORDER BY region.r_comment