SELECT region.r_comment, AVG(region.r_regionkey) AS avg_region_r_regionkey, COUNT(region.r_regionkey) AS count_region_r_regionkey, VARIANCE(region.r_regionkey) AS variance_region_r_regionkey, MIN(region.r_regionkey) AS min_region_r_regionkey, MAX(region.r_regionkey) AS max_region_r_regionkey FROM region WHERE region.r_comment = 'FUUAI' AND region.r_name LIKE 'A%' GROUP BY region.r_comment ORDER BY region.r_comment