SELECT customer.c_phone, customer.c_name, SUM(customer.c_nationkey) AS sum_customer_c_nationkey, MIN(customer.c_acctbal) AS min_customer_c_acctbal, COUNT(customer.c_acctbal) AS count_customer_c_acctbal, AVG(customer.c_nationkey) AS avg_customer_c_nationkey, STDDEV(customer.c_acctbal) AS stddev_customer_c_acctbal FROM customer WHERE customer.c_comment LIKE 'A%' GROUP BY customer.c_phone, customer.c_name ORDER BY customer.c_phone, customer.c_name