SELECT orders.o_orderdate, AVG(orders.o_shippriority) AS avg_orders_o_shippriority, AVG(orders.o_totalprice) AS avg_orders_o_totalprice, MIN(orders.o_shippriority) AS min_orders_o_shippriority, COUNT(orders.o_shippriority) AS count_orders_o_shippriority FROM orders WHERE (orders.o_orderkey BETWEEN 83 AND 908 AND orders.o_orderpriority = 'DPAJS') OR (orders.o_orderstatus LIKE 'A%') GROUP BY orders.o_orderdate HAVING COUNT(*) > 6825 AND AVG(orders.o_totalprice) > 5610 ORDER BY orders.o_orderdate