SELECT region.r_name, AVG(region.r_regionkey) AS avg_region_r_regionkey, COUNT(region.r_regionkey) AS count_region_r_regionkey, MAX(region.r_regionkey) AS max_region_r_regionkey, MIN(region.r_regionkey) AS min_region_r_regionkey FROM region WHERE (region.r_name LIKE 'A%' AND region.r_regionkey = 440) OR (region.r_comment = 'WSHUK') GROUP BY region.r_name HAVING COUNT(*) > 8033 AND AVG(region.r_regionkey) > 67705 ORDER BY region.r_name