SELECT partsupp.ps_comment, VARIANCE(partsupp.ps_supplycost) AS variance_partsupp_ps_supplycost, SUM(partsupp.ps_availqty) AS sum_partsupp_ps_availqty, SUM(partsupp.ps_supplycost) AS sum_partsupp_ps_supplycost, MAX(partsupp.ps_availqty) AS max_partsupp_ps_availqty FROM partsupp WHERE partsupp.ps_availqty < 38 AND partsupp.ps_suppkey BETWEEN 523 AND 548 GROUP BY partsupp.ps_comment ORDER BY partsupp.ps_comment