SELECT lineitem.l_linestatus, STDDEV(lineitem.l_linenumber) AS stddev_lineitem_l_linenumber, AVG(lineitem.l_orderkey) AS avg_lineitem_l_orderkey, VARIANCE(lineitem.l_extendedprice) AS variance_lineitem_l_extendedprice FROM lineitem WHERE lineitem.l_suppkey > 968 AND lineitem.l_orderkey = 756 GROUP BY lineitem.l_linestatus HAVING COUNT(*) > 7395 AND AVG(lineitem.l_orderkey) > 75740 ORDER BY lineitem.l_linestatus