SELECT orders.o_comment, COUNT(orders.o_shippriority) AS count_orders_o_shippriority, MIN(orders.o_shippriority) AS min_orders_o_shippriority, STDDEV(orders.o_orderkey) AS stddev_orders_o_orderkey, MIN(orders.o_orderkey) AS min_orders_o_orderkey, MIN(orders.o_shippriority) AS min_orders_o_shippriority FROM orders WHERE (orders.o_clerk = 'TJMRR') OR (orders.o_clerk = 'YMBOV') GROUP BY orders.o_comment ORDER BY orders.o_comment