SELECT partsupp.ps_comment, COUNT(partsupp.ps_partkey) AS count_partsupp_ps_partkey, AVG(partsupp.ps_partkey) AS avg_partsupp_ps_partkey FROM partsupp WHERE (partsupp.ps_availqty > 516 AND partsupp.ps_availqty BETWEEN 926 AND 991) OR (partsupp.ps_comment = 'DWZFQ') GROUP BY partsupp.ps_comment HAVING COUNT(*) > 5703 AND COUNT(partsupp.ps_partkey) > 57502 ORDER BY partsupp.ps_comment