SELECT supplier.s_phone, MAX(supplier.s_suppkey) AS max_supplier_s_suppkey, AVG(supplier.s_acctbal) AS avg_supplier_s_acctbal, COUNT(supplier.s_suppkey) AS count_supplier_s_suppkey, COUNT(supplier.s_acctbal) AS count_supplier_s_acctbal, COUNT(supplier.s_acctbal) AS count_supplier_s_acctbal FROM supplier WHERE supplier.s_nationkey < 87 AND supplier.s_name LIKE 'A%' AND supplier.s_address = 'EKYTA' GROUP BY supplier.s_phone ORDER BY supplier.s_phone