SELECT customer.c_comment, customer.c_phone, COUNT(customer.c_acctbal) AS count_customer_c_acctbal, SUM(customer.c_nationkey) AS sum_customer_c_nationkey, MIN(customer.c_nationkey) AS min_customer_c_nationkey FROM customer WHERE customer.c_custkey BETWEEN 144 AND 907 AND customer.c_mktsegment = 'PIOCW' AND customer.c_mktsegment = 'ZOYBU' AND customer.c_mktsegment LIKE 'A%' GROUP BY customer.c_comment, customer.c_phone ORDER BY customer.c_comment, customer.c_phone