SELECT part.p_brand, part.p_container, part.p_comment, COUNT(part.p_retailprice) AS count_part_p_retailprice, COUNT(part.p_size) AS count_part_p_size, MAX(part.p_retailprice) AS max_part_p_retailprice FROM part WHERE (part.p_comment LIKE '%B%' AND part.p_size < 969) OR (part.p_partkey < 147) GROUP BY part.p_brand, part.p_container, part.p_comment HAVING COUNT(*) > 7681 AND MAX(part.p_retailprice) > 70723 ORDER BY part.p_comment, part.p_brand